import './App.css';
import { useState } from 'react';
import profile from './profile.jpg';



export default function App() {
  let isLogged = true;
  let content;
  if(isLogged){
    content = (
      <div>
      <h1>{user.name}</h1>
      <img 
        className='avatar'
        src={user.img}
        alt={'image for '+ user.name}
        style={{
          width: user.imgSize,
          height: user.imgSize
        }}
      />
    <Btn name={"button1"}/>
    <Btn name={"button2"}/>
    <Btn name={"button3"}/>
    </div>
    );
  }else{
    content = (
      <div>
      <h1>Login Pls</h1>
    </div>
    );
  }

  return  content;
}

function Btn({name}) {
    const [count , setCount]= useState(0);

    function alertSomething(){
      setCount(count + 1);
      alert(count);
    }

    return (
      <button className='squareBtn' onClick={alertSomething}>
        {name}
      </button>

    );
  }

const user = {
  name : "shekil",
  age : 21,
  img : profile,
  imgSize : 90
};
